import React, {Component} from 'react';

class Forms extends Component {
  constructor(props){
    super(props)
    this.state= {
      counter: 5,
      index:0
    }
  }
handleResizer(evento) { //pra descon {target: {value}}
  this.setState({counter: evento.target.value})

}

  render() {
    return(
      <form>
        <input type='text' id='sizeTable' onChange={this.handleResizer} placeholder='digite a resolução do desenho'/>
        <button type='button' onClick={this.handleResizer}>Alterar</button>
        <button type='button'>Apagar</button>
      </form>
    )

  }
}

export default Forms;
