import React, {Component} from 'react';
import Pixel from './Pixel';

class Line extends Component {
  constructor(props) {
    super(props)
    this.state={
      tableArray: [],
    }
  }
  render() {

    const { lineArray } =this.props;

    return(
      <>
      {lineArray.map((item, i) => {
        return(
          <tr id={item[i]}>{item}<Pixel pixel={ lineArray}/></tr>
          )
      })}

      </>
    )
  }
}

export default Line;
